import React from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import ReactTooltip from "react-tooltip";
import { connect } from "react-redux";
import { setLines, setWords } from "../../../../actions";

interface NumWordsFilterProps {
  isSpreeding: boolean;
  words: number;
  lines: number;
  setLines(numLines: number): any;
  setWords(numWords: number): any;
}

interface NumWordsFilterState {
  isHovered: boolean;
}

class NumWordsFilter extends React.Component<
  NumWordsFilterProps,
  NumWordsFilterState
> {
  constructor(props) {
    super(props);
    this.state = {
      isHovered: false
    };
    this.setHover = this.setHover.bind(this);
    this.pause = this.pause.bind(this);
    this.increment = this.increment.bind(this);
    this.decrement = this.decrement.bind(this);
  }

  increment(type: string) {
    this.pause();
    switch (type) {
      case "LINES":
        this.props.setLines(this.props.lines + 1);
      case "WORDS":
        this.props.setWords(this.props.words + 1);
    }
  }

  decrement(type: string) {
    this.pause();
    switch (type) {
      case "LINES":
        this.props.setLines(this.props.lines - 1);
      case "WORDS":
        this.props.setWords(this.props.words - 1);
    }
  }
  pause() {
    const { isSpreeding } = this.props;
    const playPauseButton = document.querySelector("#playPause") as HTMLElement;
    if (isSpreeding) {
      playPauseButton.click();
    }
  }

  setHover(isHovered: boolean) {
    this.setState({
      isHovered
    });
  }

  render() {
    const { words, lines } = this.props;
    const { isHovered } = this.state;
    return (
      <li
        onMouseEnter={() => this.setHover(true)}
        onMouseLeave={() => this.setHover(false)}
      >
        <a
          style={{
            cursor: "pointer"
          }}
          data-tip="Words per line"
        >
          {isHovered ? (
            <span>
              <div>
                {" "}
                {isHovered && (
                  <span
                    style={{
                      position: "absolute",
                      paddingRight: "10%"
                    }}
                    onClick={() => this.decrement("LINES")}
                  >
                    <FontAwesomeIcon icon="caret-left" size="lg" />
                  </span>
                )}
                {isHovered && (
                  <span
                    style={{
                      position: "absolute",
                      paddingLeft: "10%"
                    }}
                    onClick={() => this.increment("LINES")}
                  >
                    <FontAwesomeIcon icon="caret-right" size="lg" />
                  </span>
                )}
                {/* {lines + " lines"} */}
              </div>
              <div>
                {" "}
                {isHovered && (
                  <span
                    style={{
                      position: "absolute",
                      paddingRight: "10%"
                    }}
                    onClick={() => this.decrement("WORDS")}
                  >
                    <FontAwesomeIcon icon="caret-left" size="lg" />
                  </span>
                )}
                {isHovered && (
                  <span
                    style={{
                      position: "absolute",
                      paddingLeft: "10%"
                    }}
                    onClick={() => this.increment("WORDS")}
                  >
                    <FontAwesomeIcon icon="caret-right" size="lg" />
                  </span>
                )}
                {words + " words"}
              </div>
            </span>
          ) : (
            <FontAwesomeIcon icon={["fas", "chart-line"]} />
          )}
        </a>
        <ReactTooltip place="top" type="dark" effect="float" />
      </li>
    );
  }
}

const mapStateToProps = state => {
  return {
    lines: state.lines,
    words: state.words,
    isSpreeding: state.spreeding
  };
};

const mapDispatchToProps = dispatch => {
  return {
    setLines: numLines => dispatch(setLines(numLines)),
    setWords: numWords => dispatch(setWords(numWords))
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(NumWordsFilter);
