import React from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { connect } from "react-redux";
import { setWPM } from "../../../../actions";
import ReactTooltip from "react-tooltip";

interface ChangeSpeedProps {
  wpm: number;
  increment(wpm: number): any;
  decrement(wpm: number): any;
}

interface ChangeSpeedState {
  isHovered: boolean;
}

class ChangeSpeed extends React.Component<ChangeSpeedProps, ChangeSpeedState> {
  constructor(props) {
    super(props);
    this.state = {
      isHovered: false
    };
    this.setHover = this.setHover.bind(this);
  }

  setHover(isHovered: boolean) {
    this.setState({
      isHovered
    });
  }

  render() {
    const { wpm, increment, decrement } = this.props;
    const { isHovered } = this.state;
    return (
      <li
        onMouseEnter={() => this.setHover(true)}
        onMouseLeave={() => this.setHover(false)}
      >
        <a>
          {isHovered && (
            <span
              style={{
                position: "absolute",
                paddingRight: "10%"
              }}
              onClick={decrement(wpm - 10)}
            >
              <FontAwesomeIcon icon="caret-left" />
            </span>
          )}
          {isHovered && (
            <span
              style={{
                position: "absolute",
                paddingLeft: "10%"
              }}
              onClick={increment(wpm + 10)}
            >
              <FontAwesomeIcon icon="caret-right" />
            </span>
          )}
          {isHovered ? wpm + " WPM" : <FontAwesomeIcon icon="tachometer-alt" />}
        </a>
      </li>
    );
  }
}

const mapStateToProps = state => {
  return {
    wpm: state.wpm
  };
};

const mapDispatchToProps = dispatch => {
  return {
    decrement: wpm => dispatch(setWPM(wpm)),
    increment: wpm => dispatch(setWPM(wpm))
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(ChangeSpeed);
