import React from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { connect } from "react-redux";
import { setWPM } from "../../../../actions";

interface ChangeSpeedState {
  isHovered: boolean;
}

class ChangeSpeed extends React.Component<any, ChangeSpeedState> {
  constructor(props) {
    super(props);
    this.state = {
      isHovered: false
    };
    this.setHover = this.setHover.bind(this);
  }

  setHover(isHovered: boolean) {
    this.setState({
      isHovered
    });
  }

  render() {
    const { isHovered } = this.state;
    return (
      <li
        onMouseEnter={() => this.setHover(true)}
        onMouseLeave={() => this.setHover(false)}
      >
        <a
          data-tip="Change Speed"
          style={{
            cursor: "pointer"
          }}
        >
          {isHovered && (
            <span
              style={{
                position: "absolute",
                paddingRight: "10%"
              }}
            >
              <FontAwesomeIcon icon="caret-left" />
            </span>
          )}
          {isHovered && (
            <span
              style={{
                position: "absolute",
                paddingLeft: "10%"
              }}
            >
              <FontAwesomeIcon icon="caret-right" />
            </span>
          )}
          <FontAwesomeIcon icon="tachometer-alt" />)
        </a>
        <ReactTooltip place="top" type="dark" effect="float" />{" "}
      </li>
    );
  }
}

const mapStateToProps = state => {
  return {
    wpm: state.wpm
  };
};

const mapDispatchToProps = (dispatch, ownProps) => {
  return {
    decrement: () => dispatch(setWPM(ownProps.wpm - 10)),
    increment: () => dispatch(setWPM(ownProps.wpm + 10))
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(ChangeSpeed);
