import React from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { IconProp } from "@fortawesome/fontawesome-svg-core";
import Caret from "./Caret";
import { string } from "prop-types";

interface NumProps {
  handleDecrement(): any;
  handleIncrement(): any;
  label: string;
  icon: IconProp;
}

interface NumState {
  isHovered: boolean;
}

class Num extends React.Component<NumProps, NumState> {
  constructor(props) {
    super(props);
    this.state = {
      isHovered: false
    };
    this.setHover = this.setHover.bind(this);
  }

  setHover(isHovered: boolean) {
    this.setState({
      isHovered
    });
  }

  render() {
    const { handleDecrement, handleIncrement, label, icon } = this.props;
    const { isHovered } = this.state;
    return (
      <li
        onMouseEnter={() => this.setHover(true)}
        onMouseLeave={() => this.setHover(false)}
        onClick={e => {
          // console.log(e.target);
          //caret
          let tag = (e.target as HTMLElement).tagName;
          console.log(tag);
          if (tag === "SPAN" || tag === "SVG" || tag === "PATH") {
            //left
            // console.log((e.target as HTMLElement).style.color);
            if ((e.target as HTMLElement).style.color === "black") {
              handleDecrement();
            } else {
              //right
              handleIncrement();
            }
          }
        }}
      >
        <a>
          <Caret isHovered={isHovered} direction="left" />
          <Caret isHovered={isHovered} direction="right" />
          {isHovered ? label : <FontAwesomeIcon icon={icon} />}
        </a>
      </li>
    );
  }
}

export default Num;
