/*global chrome*/
import React from "react";
import { connect } from "react-redux";
import PlayPause from "./PlayPause";

interface SpreederProps {
  textArray: string[];
}

interface SpreederState {
  position: number;
  isSpreeding: boolean;
}

class Spreeder extends React.Component<SpreederProps, SpreederState> {
  constructor(props) {
    super(props);
    this.state = {
      position: 0,
      isSpreeding: false
    };
  }

  render() {
    const { textArray } = this.props;
    const { position, isSpreeding } = this.state;
    return (
      <div>
        <div
          className="container has-text-centered"
          style={{
            position: "absolute",
            top: "44vh",
            width: "-webkit-fill-available"
          }}
        >
          {" "}
          {textArray[position]}{" "}
        </div>
        <div className="hero-foot">
          <nav className="tabs is-boxed is-fullwidth">
            <div className="container">
              <ul>
                <PlayPause />

                <li>
                  <a>Modifiers</a>
                </li>
                <li>
                  <a>Grid</a>
                </li>
                <li>
                  <a>Elements</a>
                </li>
                <li>
                  <a>Components</a>
                </li>
                <li>
                  <a>Layout</a>
                </li>
              </ul>
            </div>
          </nav>
        </div>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    textArray: state.text
      ? state.text
          .trim()
          .replace(/\s\s+/g, " ")
          .split(/\s/)
      : []
  };
};

export default connect(
  mapStateToProps,
  null
)(Spreeder);
