/*global chrome*/
import React from "react";
import { connect } from "react-redux";
import PlayPause from "./PlayPause";
import LightsOff from "./LightsOff";
import ChangeSpeed from "./ChangeSpeed";
import NumWordsFilter from "./NumWordsFilter";
import FontSize from "./FontSize";

interface SpreederProps {
  textArray: string[];
  position: number;
}

class Spreeder extends React.Component<SpreederProps> {
  constructor(props) {
    super(props);
  }

  render() {
    const { textArray, position } = this.props;
    return (
      <div>
        <div
          className="container has-text-centered"
          style={{
            position: "absolute",
            top: "44vh",
            width: "-webkit-fill-available"
          }}
        >
          {" "}
          {textArray[position]}{" "}
        </div>
        <div className="hero-foot">
          <nav className="tabs is-boxed is-fullwidth">
            <div className="container">
              <ul>
                <LightsOff />
                {/* <ChangeSpeed /> */}
                <PlayPause />
                <NumWordsFilter />
                <FontSize />
              </ul>
            </div>
          </nav>
        </div>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    textArray: state.text
      ? state.text
          .trim()
          .replace(/\s\s+/g, " ")
          .split(/\s/)
      : [],
    position: state.position
  };
};

export default connect(
  mapStateToProps,
  null
)(Spreeder);
