import React from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import ReactTooltip from "react-tooltip";
import { connect } from "react-redux";
import { setLines, setWords } from "../../../../actions";

interface NumWordsFilterProps {
  words: number;
  lines: number;
}

interface NumWordsFilterState {
  isHovered: boolean;
}

class NumWordsFilter extends React.Component<
  NumWordsFilterProps,
  NumWordsFilterState
> {
  constructor(props) {
    super(props);
    this.state = {
      isHovered: false
    };
    this.setHover = this.setHover.bind(this);
  }

  setHover(isHovered: boolean) {
    this.setState({
      isHovered
    });
  }

  render() {
    const { words, lines } = this.props;
    const { isHovered } = this.state;
    return (
      <li
        onMouseEnter={() => this.setHover(true)}
        onMouseLeave={() => this.setHover(false)}
      >
        <a
          style={{
            cursor: "pointer"
          }}
          data-tip="Words per line"
        >
          {isHovered ? (
            <span>
              <div>{lines + " lines"}</div>
              <div> {words + " words"}</div>
            </span>
          ) : (
            <FontAwesomeIcon icon={["fas", "chart-line"]} />
          )}
        </a>
        <ReactTooltip place="top" type="dark" effect="float" />
      </li>
    );
  }
}

const mapStateToProps = state => {
  return {
    lines: state.lines,
    words: state.words
  };
};

const mapDispatchToProps = dispatch => {
  return {
    incrementLines: numLines => dispatch(setLines(numLines)),
    decrementLines: numLines => dispatch(setLines(numLines)),
    incrementWords: numWords => dispatch(setWords(numWords)),
    decrementWords: numWords => dispatch(setWords(numWords))
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(NumWordsFilter);
