/*global chrome*/
import React from "react";
import { connect } from "react-redux";
import PlayPause from "./PlayPause";
import ChangeSpeed from "./ChangeSpeed";
import FontSize from "./FontSize";
import { partition, stringToWordArray } from "../../../utils";
import NumLines from "./NumLines";
import NumWords from "./NumWords";

interface SpreederProps {
  textArray: string[][][] | [];
  position: number;
}

class Spreeder extends React.Component<SpreederProps> {
  render() {
    const { textArray, position } = this.props;
    console.log(textArray);
    return (
      <div>
        <div
          className="has-text-centered"
          style={{
            position: "absolute",
            top: "44vh",
            width: "-webkit-fill-available"
          }}
        >
          {textArray
            ? textArray[position].map(line => {
                return <div className="title has-text-white">{line}</div>;
              })
            : "Add text to being spreeding"
              // <div className="title has-text-white">{textArray[position]}</div>
          }
        </div>
        <div className="hero-foot">
          <nav className="tabs is-boxed is-fullwidth">
            <ul>
              <ChangeSpeed />
              <NumLines />
              <PlayPause />
              <NumWords />
              <FontSize />
            </ul>
          </nav>
        </div>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    textArray: state.text
      ? partition(state.lines * state.words, stringToWordArray(state.text)).map(
          block => {
            return partition(state.words, block);
          }
        )
      : [],
    position: state.position
  };
};

export default connect(
  mapStateToProps,
  null
)(Spreeder);
