import React from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { connect } from "react-redux";
import { setPosition } from "../../../../actions";
import { wordsPerMinToInterval } from "../../../../utils";

interface PlayPauseProps {
  textArray: string[];
  position: number;
  increment(): any;
  reset(): any;
  wpm: number;
}
interface PlayPauseState {
  isSpreeding: boolean;
}

class PlayPause extends React.Component<PlayPauseProps, PlayPauseState> {
  timer: any;
  constructor(props) {
    super(props);
    this.state = {
      isSpreeding: false
    };
    this.startTimer = this.startTimer.bind(this);
    this.stopTimer = this.stopTimer.bind(this);
  }

  startTimer() {
    this.timer = setInterval(
      () => this.tick(),
      wordsPerMinToInterval(this.propswpm)
    );
    this.setState({
      isSpreeding: true
    });
  }

  stopTimer() {
    clearInterval(this.timer);
    this.setState({
      isSpreeding: false
    });
  }

  componentWillUnmount() {
    clearInterval(this.timer);
    this.setState({
      isSpreeding: false
    });
  }

  tick() {
    const { textArray, position, increment, reset } = this.props;
    if (position < textArray.length) {
      increment();
    } else {
      this.stopTimer();
      reset();
    }
  }

  render() {
    const { textArray } = this.props;
    const { isSpreeding } = this.state;
    return (
      <li>
        {" "}
        {isSpreeding ? (
          <a
            data-tip="Pause"
            onClick={this.stopTimer}
            style={{
              cursor: "pointer"
            }}
          >
            {" "}
            <FontAwesomeIcon icon="pause" />{" "}
          </a>
        ) : (
          <a
            data-tip={textArray.length ? "Spreed!" : "Add text to start"}
            onClick={() => {
              if (textArray.length) {
                this.startTimer();
              }
            }}
            style={{
              cursor: textArray.length ? "pointer" : "not-allowed"
            }}
          >
            {" "}
            <FontAwesomeIcon icon="play" />{" "}
          </a>
        )}
      </li>
    );
  }
}

const mapStateToProps = state => {
  return {
    textArray: state.text
      ? state.text
          .trim()
          .replace(/\s\s+/g, " ")
          .split(/\s/)
      : [],
    position: state.position,
    wpm: state.wpm
  };
};

const mapDispatchToProps = dispatch => {
  return {
    increment: () => dispatch(setPosition("INCREMENT")),
    reset: () => dispatch(setPosition("RESET"))
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(PlayPause);
