import React from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

class PlayPause extends React.Component {
  timer: any;
  constructor(props) {
    super(props);
    this.state = {
      isSpreeding: false
    };
    this.startTimer = this.startTimer.bind(this);
    this.stopTimer = this.stopTimer.bind(this);
  }

  startTimer() {
    this.timer = setInterval(() => this.tick(), 1000);
    this.setState({
      isSpreeding: true
    });
  }

  stopTimer() {
    clearInterval(this.timer);
    this.setState({
      isSpreeding: false
    });
  }

  componentWillUnmount() {
    clearInterval(this.timer);
    this.setState({
      isSpreeding: false
    });
  }

  tick() {
    const { textArray } = this.props;
    const { position } = this.state;
    if (position < textArray.length) {
      this.setState({
        position: position + 1
      });
    } else {
      this.stopTimer();
      this.setState({
        position: 0
      });
    }
  }

  render() {
    return (
      <li>
        <a>
          {" "}
          {isSpreeding ? (
            <div>
              <span
                onClick={this.stopTimer}
                style={{
                  cursor: "pointer"
                }}
              >
                {" "}
                <FontAwesomeIcon icon="pause" />{" "}
              </span>
            </div>
          ) : (
            <div>
              <span
                onClick={() => {
                  if (textArray.length) {
                    this.startTimer();
                  }
                }}
                style={{
                  cursor: textArray.length ? "pointer" : "not-allowed"
                }}
              >
                {" "}
                <FontAwesomeIcon icon="play" />{" "}
              </span>
            </div>
          )}
        </a>
      </li>
    );
  }
}
