/*global chrome*/
import React from "react";
import Navbar from "../Navbar";
import { connect } from "react-redux";

interface SpreederProps {
  active: boolean;
  textArray: string[];
}

interface SpreederState {
  position: number;
}

class Spreeder extends React.Component<SpreederProps, SpreederState> {
  timer: any;
  constructor(props) {
    super(props);
    this.state = {
      position: 0
    };
  }

  componentDidMount() {
    this.timer = setInterval(() => this.tick(), 1000);
  }

  componentWillUnmount() {
    clearInterval(this.timer);
  }

  tick() {
    this.setState({
      position:
        this.state.position < this.props.textArray.length
          ? this.state.position + 1
          : this.state.position
    });
  }

  render() {
    const { active, textArray } = this.props;
    const { position } = this.state;
    return (
      <section
        className="hero is-fullheight"
        style={{
          display: active ? "block" : "none"
        }}
      >
        <Navbar />
        <div className="container">{textArray[position]}</div>
      </section>
    );
  }
}

const mapStateToProps = state => {
  return {
    active: state.toggle.spreed,
    textArray: state.text ? state.text.split() : []
  };
};

export default connect(
  mapStateToProps,
  null
)(Spreeder);
