import React from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { connect } from "react-redux";
import { setPosition } from "../../../../actions";

interface PlayPauseProps {
  textArray: string[];
  position: number;
}
interface PlayPauseState {
  isSpreeding: boolean;
}

class PlayPause extends React.Component<PlayPauseProps, PlayPauseState> {
  timer: any;
  constructor(props) {
    super(props);
    this.state = {
      isSpreeding: false
    };
    this.startTimer = this.startTimer.bind(this);
    this.stopTimer = this.stopTimer.bind(this);
  }

  startTimer() {
    this.timer = setInterval(() => this.tick(), 1000);
    this.setState({
      isSpreeding: true
    });
  }

  stopTimer() {
    clearInterval(this.timer);
    this.setState({
      isSpreeding: false
    });
  }

  componentWillUnmount() {
    clearInterval(this.timer);
    this.setState({
      isSpreeding: false
    });
  }

  tick() {
    const { textArray, position } = this.props;
    if (position < textArray.length) {
      this.setState({
        position: position + 1
      });
    } else {
      this.stopTimer();
      this.setState({
        position: 0
      });
    }
  }

  render() {
    return (
      <li>
        <a>
          {" "}
          {isSpreeding ? (
            <div>
              <span
                onClick={this.stopTimer}
                style={{
                  cursor: "pointer"
                }}
              >
                {" "}
                <FontAwesomeIcon icon="pause" />{" "}
              </span>
            </div>
          ) : (
            <div>
              <span
                onClick={() => {
                  if (textArray.length) {
                    this.startTimer();
                  }
                }}
                style={{
                  cursor: textArray.length ? "pointer" : "not-allowed"
                }}
              >
                {" "}
                <FontAwesomeIcon icon="play" />{" "}
              </span>
            </div>
          )}
        </a>
      </li>
    );
  }
}

const mapStateToProps = state => {
  return {
    textArray: state.text
      ? state.text
          .trim()
          .replace(/\s\s+/g, " ")
          .split(/\s/)
      : [],
    position: state.position
  };
};

const mapDispatchToProps = dispatch => {
  return {
    increment: () => dispatch(setPosition("INCREMENT"))
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(PlayPause);
