import React from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { IconProp } from "@fortawesome/fontawesome-svg-core";
import Caret from "./Caret";
import { string } from "prop-types";

interface NumProps {
  handleDecrement(): any;
  handleIncrement(): any;
  label: string;
  icon: IconProp;
}

interface NumState {
  isHovered: boolean;
}

class Num extends React.Component<NumProps, NumState> {
  constructor(props) {
    super(props);
    this.state = {
      isHovered: false
    };
    this.setHover = this.setHover.bind(this);
  }

  setHover(isHovered: boolean) {
    this.setState({
      isHovered
    });
  }

  render() {
    const { handleDecrement, handleIncrement, label, icon } = this.props;
    const { isHovered } = this.state;
    return (
      <li
        onMouseEnter={() => this.setHover(true)}
        onMouseLeave={() => this.setHover(false)}
        onClick={e => {
          //caret
          const tag = (e.target as HTMLElement).tagName;
          if (tag === "svg" || tag === "path" || tag === "a") {
            //right
            handleIncrement();
          } else {
            console.log(e.target);
          }
        }}
      >
        <a
          onClick={e => {
            console.log(e.target);
          }}
        >
          <Caret
            isHovered={isHovered}
            direction="left"
            handleClick={handleDecrement}
          />
          <Caret isHovered={isHovered} direction="right" />
          {isHovered ? label : <FontAwesomeIcon icon={icon} />}
        </a>
      </li>
    );
  }
}

export default Num;
