import React from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { connect } from "react-redux";
import { setWPM } from "../../../../actions";

interface ChangeSpeedProps {
  wpm: number;
  incrementWPM(wpm: number): any;
  decrementWPM(wpm: number): any;
  isSpreeding: boolean;
}

interface ChangeSpeedState {
  isHovered: boolean;
}

class ChangeSpeed extends React.Component<ChangeSpeedProps, ChangeSpeedState> {
  constructor(props) {
    super(props);
    this.state = {
      isHovered: false
    };
    this.setHover = this.setHover.bind(this);
    this.increment = this.increment.bind(this);
    this.decrement = this.decrement.bind(this);
    this.pause = this.pause.bind(this);
  }

  setHover(isHovered: boolean) {
    this.setState({
      isHovered
    });
  }

  pause() {
    const { isSpreeding } = this.props;
    const playPauseButton = document.querySelector("#playPause") as HTMLElement;
    if (isSpreeding) {
      playPauseButton.click();
    }
  }

  increment() {
    this.pause();
    //change wpm
    this.props.incrementWPM(this.props.wpm + 10);
  }

  decrement() {
    this.pause();
    //change wpm
    this.props.decrementWPM(this.props.wpm + 10);
  }

  render() {
    const { wpm } = this.props;
    const { isHovered } = this.state;
    return (
      <li
        onMouseEnter={() => this.setHover(true)}
        onMouseLeave={() => this.setHover(false)}
      >
        <a>
          {isHovered && (
            <span
              style={{
                position: "absolute",
                paddingRight: "10%"
              }}
              onClick={this.decrement}
            >
              <FontAwesomeIcon icon="caret-left" size="lg" />
            </span>
          )}
          {isHovered && (
            <span
              style={{
                position: "absolute",
                paddingLeft: "10%"
              }}
              onClick={this.increment}
            >
              <FontAwesomeIcon icon="caret-right" size="lg" />
            </span>
          )}
          {isHovered ? wpm + " WPM" : <FontAwesomeIcon icon="tachometer-alt" />}
        </a>
      </li>
    );
  }
}

const mapStateToProps = state => {
  return {
    wpm: state.wpm,
    isSpreeding: state.spreeding
  };
};

const mapDispatchToProps = dispatch => {
  return {
    decrementWPM: wpm => dispatch(setWPM(wpm)),
    incrementWPM: wpm => dispatch(setWPM(wpm))
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(ChangeSpeed);
