/*global chrome*/
import React from "react";
import { connect } from "react-redux";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

interface SpreederProps {
  textArray: string[];
}

interface SpreederState {
  position: number;
  isSpreeding: boolean;
}

class Spreeder extends React.Component<SpreederProps, SpreederState> {
  timer: any;
  constructor(props) {
    super(props);
    this.state = {
      position: 0,
      isSpreeding: false
    };
    this.startTimer = this.startTimer.bind(this);
    this.stopTimer = this.stopTimer.bind(this);
  }

  startTimer() {
    this.timer = setInterval(() => this.tick(), 1000);
    this.setState({
      isSpreeding: true
    });
  }

  stopTimer() {
    clearInterval(this.timer);
    this.setState({
      isSpreeding: false
    });
  }

  componentWillUnmount() {
    clearInterval(this.timer);
    this.setState({
      isSpreeding: false
    });
  }

  tick() {
    const { textArray } = this.props;
    const { position } = this.state;
    if (position < textArray.length) {
      this.setState({
        position: position + 1
      });
    } else {
      this.stopTimer();
      this.setState({
        position: 0
      });
    }
  }

  render() {
    const { textArray } = this.props;
    const { position, isSpreeding } = this.state;
    return (
      <div
      // style={{
      //   position: "absolute",
      //   top: "44vh",
      //   width: "-webkit-fill-available"
      // }}
      >
        <div className="container has-text-centered">
          {" "}
          {textArray[position]}{" "}
        </div>
        <nav className="level">
          <div className="level-item has-text-centered">
            {isSpreeding ? (
              <div>
                <span
                  onClick={this.stopTimer}
                  style={{
                    cursor: "pointer"
                  }}
                >
                  {" "}
                  <FontAwesomeIcon icon="pause" />{" "}
                </span>
              </div>
            ) : (
              <div>
                <span
                  onClick={() => {
                    if (textArray.length) {
                      this.startTimer();
                    }
                  }}
                  style={{
                    cursor: textArray.length ? "pointer" : "not-allowed"
                  }}
                >
                  {" "}
                  <FontAwesomeIcon icon="play" />{" "}
                </span>
              </div>
            )}
          </div>
        </nav>
        <div className="hero-body">
          <div className="container has-text-centered">
            <h1 className="title">Title</h1>
            <h2 className="subtitle">Subtitle</h2>
          </div>
        </div>
        <div className="hero-foot">
          <nav className="tabs is-boxed is-fullwidth">
            <div className="container">
              <ul>
                <li className="is-active">
                  <a>Overview</a>
                </li>
                <li>
                  <a>Modifiers</a>
                </li>
                <li>
                  <a>Grid</a>
                </li>
                <li>
                  <a>Elements</a>
                </li>
                <li>
                  <a>Components</a>
                </li>
                <li>
                  <a>Layout</a>
                </li>
              </ul>
            </div>
          </nav>
        </div>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    textArray: state.text
      ? state.text
          .trim()
          .replace(/\s\s+/g, " ")
          .split(/\s/)
      : []
  };
};

export default connect(
  mapStateToProps,
  null
)(Spreeder);
