/*global chrome*/
import React from "react";
import Navbar from "../Navbar";
import { connect } from "react-redux";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

interface SpreederProps {
  active: boolean;
  textArray: string[];
}

interface SpreederState {
  position: number;
}

class Spreeder extends React.Component<SpreederProps, SpreederState> {
  timer: any;
  constructor(props) {
    super(props);
    this.state = {
      position: 0
    };
    this.startTimer = this.startTimer.bind(this);
    this.stopTimer = this.stopTimer.bind(this);
  }

  startTimer() {
    this.timer = setInterval(() => this.tick(), 1000);
  }

  stopTimer() {
    clearInterval(this.timer);
  }

  componentWillUnmount() {
    clearInterval(this.timer);
  }

  tick() {
    const { textArray } = this.props;
    const { position } = this.state;
    this.setState({
      position: position < textArray.length ? position + 1 : position
    });
  }

  render() {
    const { active, textArray } = this.props;
    const { position } = this.state;
    return (
      <section
        className="hero is-fullheight"
        style={{
          display: active ? "block" : "none"
        }}
      >
        <Navbar />
        <div
          style={{
            position: "absolute",
            top: "44vh"
          }}
        >
          <div className="container"> {textArray[position]} </div>
          <nav className="level">
            <div className="level-item has-text-centered">
              <div>
                <span onClick={this.startTimer}>
                  {" "}
                  <FontAwesomeIcon icon="play" />{" "}
                </span>
              </div>
            </div>
            <div className="level-item has-text-centered">
              <div>
                <span onClick={this.stopTimer}>
                  {" "}
                  <FontAwesomeIcon icon="pause" />{" "}
                </span>
              </div>
            </div>
          </nav>
        </div>
      </section>
    );
  }
}

const mapStateToProps = state => {
  return {
    active: state.toggle.spreed,
    textArray: state.text
      ? state.text
          .trim()
          .replace(/\s\s+/g, " ")
          .split(/\s/)
      : []
  };
};

export default connect(
  mapStateToProps,
  null
)(Spreeder);
