/*global chrome*/
import React from "react";
import Navbar from "../Navbar";
import { connect } from "react-redux";

interface SpreederProps {
  active: boolean;
}

interface SpreederState {
  position: number;
}

class Spreeder extends React.Component<SpreederProps, SpreederState> {
  constructor(props) {
    super(props);
    this.state = {
      position: 0
    };
  }

  componentDidMount() {
    this.timer = setInterval(() => this.tick(), 1000);
  }

  componentWillUnmount() {
    clearInterval(this.timer);
  }

  tick() {
    this.setState({
      position: this.state.position + 1
    });
  }

  render() {
    return <div>{this.state.arr[this.state.position]}</div>;
  }
  render() {
    const { active } = this.props;
    return (
      <section
        className="hero is-fullheight"
        style={{
          display: active ? "block" : "none"
        }}
      >
        <Navbar />
        <div className="container" />
      </section>
    );
  }
}

const mapStateToProps = state => {
  return {
    active: state.toggle.spreed,
    textArray: state.text.split()
  };
};

export default connect(
  mapStateToProps,
  null
)(Spreeder);
